import React, { useState } from "react";
import './CalculateButton.css';

const CalculateButton = (props) => {
    const [overallHours, setOverallHours] = useState('');
    let tmpTotalHours = 0;
    const doCalculation = () => {
        tmpTotalHours += props.totalMinutes;
        setOverallHours(tmpTotalHours);
        console.log(tmpTotalHours);
        
       };

    return (
        <div>
            <button onClick={doCalculation}  className="button-calculate">Calculate</button>
        </div>
    );
};

export default CalculateButton;
