import React, { useState } from 'react';
import './InputData.css';
import InputTable from './InputTable';

const InputData = (props) => {
    
    const [dayEntered, setDayEntered] = useState('');

    const [startTimeEntered, setStartTimeEntered] = useState('');
    const [offTimeEntered, setOffTimeEntered] = useState('');

    const [breakStartEntered, setBreakStartEntered] = useState('');
    const [breakFinishEntered, setBreakFinishEntered] = useState('');

    // const [addStuffButton, setAddStuffButton] = useState('');

    
    const dayChangeHandler = event => {
        setDayEntered(event.target.value);
    };

    const startTimeChangeHandler = event => {
        setStartTimeEntered(event.target.value);
    };

    const offTimeChangeHandler = event => { 
        setOffTimeEntered(event.target.value);

    };

    const breakStartChangeHandler = event => {
        setBreakStartEntered(event.target.value);
    };

    const breakFinishChangeHandler = event => {
        setBreakFinishEntered(event.target.value);
    };

    // const submitButtonHandler = () => {
    //     setAddStuffButton(dayEntered);
    // };

    const submitHandler = (event) => {
        event.preventDefault();

        const inputDataObject = {
            id: Math.random().toString(),
            day: dayEntered,
            startWorkTime: startTimeEntered,
            offWorkTime: offTimeEntered,
            breakStart: breakStartEntered,
            breakFinish: breakFinishEntered
        };

        props.onAddInputData(inputDataObject);
        // testList.push(inputDataObject);

      
    };

    

    return (
        <div class="wrapper">
        <form onSubmit={submitHandler}>
            <label class="label">
              Day:
           </label>
              <input class="input-name" type="date" onChange={dayChangeHandler} required/>
            
            <label class="label">
            Start Work Time:
                </label>
            <input class="input-name" type="time" id="appt" name="appt" min="06:00" max="22:00" onChange={startTimeChangeHandler}  required></input>
            
            <label class="label">
            Off Work Time:
            <input class="input-name"  type="time" id="appt" name="appt" min="06:00" max="22:00" onChange={offTimeChangeHandler} required></input>
            </label>
   
            <label  class="label">
            Break Start:
                </label>
            <input class="input-name"  type="time" id="appt" name="appt" min="06:00" max="22:00" onChange={breakStartChangeHandler} required></input>
            
            <label class="label">
            Break Finish:
                </label>
            <input class="input-name"  type="time" id="appt" name="appt" min="06:00" max="22:00" onChange={breakFinishChangeHandler} required></input>
            
           <input class="button-submit" type="submit" />
          </form>

        {/* <InputTable dataList={testList}  /> */}

   </div>
    );
}

export default InputData;
