import React, { useState } from 'react';
import './InputData.css';


const InputData = (props) => {
    
    const [dayEntered, setDayEntered] = useState('');

    const [startTimeEntered, setStartTimeEntered] = useState('');
    const [offTimeEntered, setOffTimeEntered] = useState('');

    const [breakStartEntered, setBreakStartEntered] = useState('');
    const [breakFinishEntered, setBreakFinishEntered] = useState('');

    const [hoursColculated, setHours] = useState('');
    const [salaryColculated, setSalary] = useState('');

    // const [addStuffButton, setAddStuffButton] = useState('');

    
    const dayChangeHandler = event => {
        setDayEntered(event.target.value);
    };

    const startTimeChangeHandler = event => {
        setStartTimeEntered(event.target.value);
    };

    const offTimeChangeHandler = event => { 
        setOffTimeEntered(event.target.value);

    };

    const breakStartChangeHandler = event => {
        setBreakStartEntered(event.target.value);
    };

    const breakFinishChangeHandler = event => {
        setBreakFinishEntered(event.target.value);
    };

    // const submitButtonHandler = () => {
    //     setAddStuffButton(dayEntered);
    // };

    const submitHandler = (event) => {
        event.preventDefault();
     
  
            // const off = offTimeEntered.split(':');
            // const start = startTimeEntered.split(':');

            // const breakFin =  breakFinishEntered.split(':');
            // const breakStar =  breakStartEntered.split(':');

  
            const startTime = new Date(dayEntered + ' ' + startTimeEntered);
            const offTime = new Date(dayEntered + ' ' + offTimeEntered);

            
            
            const breakStartTime = new Date(dayEntered + ' ' + breakStartEntered);
            const breakFinishTime = new Date(dayEntered + ' ' + breakFinishEntered);
          



        //    if(parseInt(off[1]) < parseInt(start[1])){
        //     totalResult = `${parseInt(off[0]) - parseInt(start[0])}:${(parseInt(off[1]) - parseInt(start[1])) + 60}`;
        //    } else {
        //     totalResult = `${parseInt(off[0]) - parseInt(start[0])}:${parseInt(off[1]) - parseInt(start[1])}`;
        //    }

        //    if(parseInt(breakFin[1]) < parseInt(breakStar[1])){
        //     breakResult = `${parseInt(breakFin[0]) - parseInt(breakStar[0])}:${(parseInt(breakFin[1]) - parseInt(breakStar[1])) + 60}`;
        //    } else {
        //     breakResult = `${parseInt(breakFin[0]) - parseInt(breakStar[0])}:${parseInt(breakFin[1]) - parseInt(breakStar[1])}`;
        //    }


           console.log(startTime.getMilliseconds());
           console.log(offTime.getMilliseconds());
           console.log(breakStartTime.getUTCMilliseconds);
           console.log(breakFinishTime.getMilliseconds());
     
        


        const inputDataObject = {
            id: Math.random().toString(),
            date: dayEntered,
            startWorkTime: startTimeEntered,
            offWorkTime: offTimeEntered,
            breakStart: breakStartEntered,
            breakFinish: breakFinishEntered,
            // hours: 
            // salary:
        };

        props.onAddInputData(inputDataObject);
        console.log(inputDataObject);


        setDayEntered('');
        setBreakFinishEntered('');
        setBreakStartEntered('');
        setOffTimeEntered('');
        setStartTimeEntered('');

      
    };

    

    return (
       <div>
        <form onSubmit={submitHandler}>
            <label className="label">
              Day:
           </label>
              <input className="input-name" type="date" value={dayEntered} onChange={dayChangeHandler} required/>
            
            <label className="label">
            Start Work Time:
                </label>
            <input className="input-name" type="time" value={startTimeEntered} id="appt" name="appt" min="06:00" max="22:00" onChange={startTimeChangeHandler}  required></input>
            
            <label class="label">
            Off Work Time:
            <input className="input-name"  type="time" value={offTimeEntered} id="appt" name="appt" min="06:00" max="22:00" onChange={offTimeChangeHandler} required></input>
            </label>
   
            <label  className="label">
            Break Start:
                </label>
            <input className="input-name"  type="time" value={breakStartEntered} id="appt" name="appt" min="06:00" max="22:00" onChange={breakStartChangeHandler} required></input>
            
            <label className="label">
            Break Finish:
                </label>
            <input className="input-name"  type="time" value={breakFinishEntered} id="appt" name="appt" min="06:00" max="22:00" onChange={breakFinishChangeHandler} required></input>
            
           <input className="button-submit" type="submit" />
          </form>


        </div>
    );
}

export default InputData;
