import React, { useState } from "react";
import InputData from "./components/InputData";
import './index.css';
import InputTable from './components/InputTable';
import Card from './UI/Card';
import CalculateButton from './components/CalculateButton';

const testList = [{
  id: '0',
  date: 'Date',
  startWorkTime: 'Start Work Time',
  offWorkTime: 'Off Work Time',
  breakStart: 'Break Start',
  breakFinish: 'Break Finish',
  hours: 'Hours',
  salary: 'Salary'
}];

const App = () => {
  const [testListUpdated, setTestListUpdated] = useState(testList);
  const [onlyMinutes, setOnlyMinutes] = useState('');
  const [onlySalary, setOnlySalary] = useState('');



  const addInputData = (acceptedInputDataObject, minutes, salary) => {


    setTestListUpdated([...testList, acceptedInputDataObject]);
    testList.push(acceptedInputDataObject);
    setOnlyMinutes(minutes);
    setOnlySalary(salary);

  };


  return (
    <Card>
    <InputData onAddInputData={addInputData}/>
    <InputTable dataList={testListUpdated} />
    <CalculateButton dataList={testList} totalMinutes={onlyMinutes} totalSalary={onlySalary} />
    </Card>

  );
}

export default App;
